import React from "react";
// import Microlink from "@microlink/react";

function article_1() {
	return {
		date: "7 May 2023",
		title: "The Benefits of Cloud Computing",
		description:
			"Cloud computing offers a range of benefits, including cost savings and increased flexibility. Find out why more businesses are turning to the cloud.",
		keywords: [
			"The Benefits of Cloud Computing",
			"Tharindu",
			"Tharindu N",
			"Tharindu Nayanajith",
		],
		style: `
				.article-content {
					display: flex;
					flex-direction: column;
					align-items: center;
				}

				.randImage {
					align-self: center;
					outline: 2px solid red;
				}
				`,
		body: (
			<React.Fragment>
				<div style={{ width: '100%', height: '1000px', border: '1px solid black' }}>
					 <iframe 
						src={"https://jindal.utdallas.edu/news/finhack-2024-challenges-and-encourages-ut-dallas-students/"} 
						title={"First place in FinHack 2024 (Financial Hackathon)"} 
						style={{ width: '100%', height: '100%' }} 
						frameBorder="0"
					></iframe>
				</div>
			</React.Fragment>
		),
	};
}

function article_2() {
	return {
		date: "12 August 2023",
		title: "How to Build a Simple and Elegant React Website in 30 Minutes",
		description:
			"Have you ever wanted to create your own website from scratch, but felt overwhelmed by the complexity and jargon of web development? Well now there's a simple way to do it!",
		style: ``,
		keywords: [
			"React",
			"Website framework",
			"Elegant design",
			// "Tharindu Nayanajith",
		],
		body: (
			<React.Fragment>
					 <div style={{ width: '100%', height: '1000px', border: '1px solid black' }}>
					 <iframe 
						src={"https://medium.com/@viswaretaskotra/how-to-build-a-simple-and-elegant-react-website-in-30-minutes-132b92cc0dd5"} 
						title={""} 
						style={{ width: '100%', height: '100%' }} 
						frameBorder="0"
					></iframe>
					</div>
			</React.Fragment>
		),
	};
}

const myArticles = [article_1, article_2];

export default myArticles;
